import React, { Component } from 'react'
import UserService from './../../../service/user.service'

import './UserList.css'
import UserCard from './../userCard/UserCard'
import SearchBar from './../searchBar/SearchBar'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'

class UserList extends Component {
	constructor() {
		super()
		this.state = {
            users: [],
            // filteredUsers:[]
		}
		this.userService = new UserService()
	}

	filteredSearch = str => {
		const { gamesCopy } = this.state
		const filteredresults = gamesCopy.filter(game => game.title.toLowerCase().includes(str.toLowerCase()))
		this.setState({ games: filteredresults })
	}

	getAllUsers = () => {
		this.userService.getUsers()
			.then(response => this.setState({ users: response.data }))
			.catch(err => console.log(err))
	}

	componentDidMount = () => {
        this.getAllUsers()
    }

	render() {
		return (
			<Container as='section'>
				<h1>Usuarios</h1>
				<div>
					<SearchBar filteredSearch={this.filteredSearch} />
					{!this.state.users.length && <p>No se encontraron resultados</p>}
				</div>
				<Row className='users-list'>{this.state.users.map(user => <UserCard key={user._id} {...user} />)}</Row>
			</Container>
		)
	}
}
export default UserList
