import React, { Component } from 'react'
import EventService from '../../../service/events.service'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import './EventDetails.css'

import { Link } from 'react-router-dom'

class EventDetails extends Component {

    constructor(props) {
        super(props)
        this.state = {
            event: null
        }
        this.eventService = new EventService()
    }

    getEventInfo() {
        const id = this.props.match.params.eventId
        this.eventService.getEvent(id)
            .then(response => this.setState( {event: response.data} ))
            .catch(err => console.log(err))
    }

    componentDidMount = () => {
        this.getEventInfo()
    }

    render() {
        if (this.state.event) {
            const {author, title, description, location, attendance, gameTime, gameHour, reviews} = this.state.event
            return (
                <Container as="section" className="event-details">
                    <h1>{title}</h1>

                    <Row as="article">
                        <Col md={{ span: 5, offset: 1 }}>
                            <div id="map"></div>
                            <Link to="/events" className="btn btn-success">Volver</Link>
                            {}
                            <Link to="/events" className="btn btn-success">Apuntarme</Link>
                        </Col>
                        <Col md={{ span: 5 }}>
                            <h4>Detalles</h4>
                            <hr />
                            <p> <strong>Autor: </strong> {author.username} </p>
                            <hr />
                            <p> <strong>Descripción: </strong> {description} </p>
                            <hr />
                            <p> <strong>Dirección: </strong> {location} </p>
                            <hr />
                            <p> <strong>Participantes:  </strong> {attendance} </p>
                            <hr />
                            <p> <strong>Fecha: </strong> {gameTime} </p>
                            <hr />
                            <p> <strong>Hora: </strong> {gameHour} </p>
                            <hr />
                            <p> <strong>Reseñas: </strong> {reviews} </p>
                        </Col>
                    </Row>
                </Container>
            )
        } else {
            return <p>Loading</p>
        }
    }
}
export default EventDetails
