import React, { Component } from 'react'
import UserService from './../../../service/user.service'

import './UserList.css'

import SearchBar from './../searchBar/SearchBar'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'
import Col from 'react-bootstrap/Col'


class UserList extends Component {
    constructor() {
        super()
        this.state = {
            users: []
        }
        this.userService = new UserService()
    }

    filteredSearch = str => {
		const { usersCopy } = this.state
		const filteredresults = usersCopy.filter(user => user.username.toLowerCase().includes(str.toLowerCase()))
		this.setState({ users: filteredresults })
	}

    getAllUsers = () => {
        this.userService.getUsers()
            .then(response => {
                console.log(response)
                this.setState({ users: response.data })
            } )
			.catch(err => console.log(err))

    }

    componentDidMount = () => {
        this.getAllUsers()
    }
    render() {
        return (
            <Container as="section">
                <h1>Usuarios</h1>
                <div>
					<SearchBar filteredSearch={this.filteredSearch} />
					{!this.state.games.length && <p>No se encontraron resultados</p>}
				</div>

            </Container>
        )
    }
}
export default UserList
