import React, { Component } from 'react'
import UserService from '../../../service/user.service'
import './Profile.css'
import Card from 'react-bootstrap/Card'
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'


class Profile extends Component {
    constructor(props) {
        super(props)
        this.state = {
            profileData: null
        }
        this.UserService = new UserService()
    }

    componentDidMount() {
        this.UserService.getProfileInfo(this.props.loggedInUser._id)
            .then((response) => {
                this.setState({profileData: response.data})
            })
    }


    render() {
        return (
            <>
                <h1>Â¡Bienvend@, {this.props.loggedInUser.username}!</h1>
                {
                    !this.state.profileData ? null :
                        <>
                            <h1>Mis juegos</h1>
                            <Container>
                                <Row>
                                    <Col>
                                        {
                                            this.state.profileData.games.map(game => <Card key={game._id}>{game.title}</Card> )
                                        }

                                    </Col>
                                </Row>
                            </Container>
                        </>
                }
                  {
                    !this.state.profileData ? null :
                        <>
                            <h1>Mis Eventos</h1>
                            <Container>
                                <Row>
                                    <Col md>
                                        {
                                            this.state.profileData.events.map(event => <p key={event._id}>{event.title}</p> )
                                        }
                                    </Col>
                                </Row>
                            </Container>
                        </>
                }
                   {
                    !this.state.profileData ? null :
                        <>
                            <h1>Mis reviews</h1>
                            <Container>
                                <Row>
                                    <Col>
                                        {
                                            this.state.profileData.reviews.map(review => <p key={review._id}>{review.text}</p> )
                                        }
                                    </Col>
                                </Row>
                            </Container>
                        </>
                }
            </>
        )
}
}

export default Profile
