    constructor(props) {
        super(props)
        this.state = {
            profileData: null
        }
        this.UserService = new UserService()
    }

    componentDidMount() {
        this.UserService.getProfileInfo(this.props.loggedInUser._id)
            .then((response) => {
                this.setState({profileData: response.data})
            })
    }


