import React, { Component } from 'react'
import UserService from './../../../service/user.service'

import './UserList.css'
import UserCard from './../userCard/UserCard'
import SearchBar from './../searchBar/SearchBar'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'

class UserList extends Component {
	constructor() {
		super()
		this.state = {
            users: [],
            filteredUsers:[]
		}
		this.userService = new UserService()
	}

	filteredSearch = str => {
		const filteredresults = this.state.users.filter(user => user.username.toLowerCase().includes(str.toLowerCase()))
		this.setState({ filteredUsers: filteredresults })
	}

    getUserInfo () {
        const id = this.props.match.params.userId
        this.userService.getUserById(id)
            .then(response => this.setState({ response }))
			.catch(err => console.log(err))
    }

	getAllUsers = () => {
		this.userService			.getUsers()
			.then(response => {
				console.log(response.data)
				this.setState({ users: response.data })
			})
			.catch(err => console.log(err))
	}

	componentDidMount = () => {
        this.getAllUsers()
    }

	render() {
		return (
			<Container as='section'>
				<h1>Usuarios</h1>
				<div>
					<SearchBar filteredSearch={this.filteredSearch} />
					{!this.state.users.length && <p>No se encontraron resultados</p>}
				</div>
				<Row className='users-list'>{this.state.users.map(user => <UserCard key={user._id} {...user} />)}</Row>
			</Container>
		)
	}
}
export default UserList
